CREATE DATABASE IF NOT EXISTS `tp_weyburn_blog` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
use `tp_weyburn_blog`;


-- --------------------------------------------------------
DROP TABLE IF EXISTS `categories`;
CREATE TABLE `categories` (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT '分类id',
  `name` varchar(36) NOT NULL DEFAULT '' COMMENT '分类名称',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `deleted_at` timestamp NULL DEFAULT NULL COMMENT '删除时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT '分类表';

INSERT INTO `categories` (`id`, `name`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, '转载', '2018-03-01 12:51:59', '2018-03-01 04:45:20', NULL),
(2, '收藏链接', '2018-03-01 05:13:20', '2018-03-01 05:14:51', NULL),
(3, '阅读笔记', '2018-03-01 05:21:45', '2018-03-01 05:21:45', NULL),
(4, '随笔', '2018-03-01 11:50:59', '2018-03-01 11:50:59', NULL);
-- --------------------------------------------------------


-- --------------------------------------------------------
DROP TABLE IF EXISTS `comments`;
CREATE TABLE `comments` (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT '评论id',
  `name` varchar(40) NOT NULL DEFAULT '' COMMENT '评论者名称',
  `email` varchar(64) NOT NULL DEFAULT '' COMMENT '评论者邮箱',
  `website` varchar(120) NULL DEFAULT NULL COMMENT '评论者网址',
  `ip` varchar(40) NULL DEFAULT NULL COMMENT '评论者ip地址',
  `content` varchar(300) NOT NULL DEFAULT '' COMMENT '评论内容',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态(0为未读,1为已读)',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `deleted_at` timestamp NULL DEFAULT NULL COMMENT '删除时间',
  `post_id` int(10) UNSIGNED NULL DEFAULT NULL COMMENT '博客id',
  `reply_id` int(10) UNSIGNED NULL DEFAULT NULL COMMENT '回复id',
  `reply_name` varchar(40) NULL DEFAULT NULL COMMENT '回复名称',
  FOREIGN KEY (`post_id`) REFERENCES posts(`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='评论表';
-- --------------------------------------------------------


-- --------------------------------------------------------
DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts`(
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT '文章id',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '文章标题',
  `content` text NOT NULL DEFAULT '' COMMENT '文章内容',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态(0为草稿,1为发布)',
  `view_count` int(6) UNSIGNED NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `deleted_at` timestamp NULL DEFAULT NULL COMMENT '删除时间',
  `published_at` timestamp NULL DEFAULT NULL COMMENT '发布时间',
  `category_id` int(10) UNSIGNED NULL DEFAULT NULL COMMENT '分类id',
  FOREIGN KEY (`category_id`) REFERENCES categories(`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT '文章表';
INSERT INTO `posts` (`id`, `title`, `content`, `status`, `view_count`, `created_at`, `updated_at`, `deleted_at`, `published_at`, `category_id`) VALUES
(1, '如何优雅的选择字体(font-family)', '<p>大家都知道，在不同操作系统、不同游览器里面默认显示的字体是不一样的，并且相同字体在不同操作系统里面渲染的效果也不尽相同，那么如何设置字体显示效果会比较好呢？下面我们逐步的分析一下：</p>\r\n<h3>一、首先我们看看各平台的默认字体情况</h3>\r\n<h5>1、Window下：</h5>\r\n<ul><a>\r\n <li>\r\n    <p><strong>宋体（SimSun）</strong>：Win 下大部分游览器的默认字体，<code>宋体</code>在小字号下（如 12px、14px）的显示效果还可以接受，但是字号一大就非常糟糕了，所以使用的时候要注意。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>微软雅黑（\"Microsoft Yahei\"）</strong>：从 Vista 开始，微软提供了这款新的字体，一款无衬线的黑体类字体，并且拥有 <em>Regular</em>、<em>Bold</em> 两种粗细的字重，显著提高了字体的显示效果。现在这款字体已经成为 Windows 游览器中最值得使用的中文字体。从 Win8 开始，<code>微软雅黑</code>又加入了 <em>Light</em> 这款更细的字重，对于喜欢细字体的设计、开发人员又多了一个新的选择。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>Arial</strong>：Win 平台上默认的无衬线西文字体（为什么要说英文字体后面会解释），有多种变体，显示效果一般。</p>\r\n </li>\r\n <li>\r\n    <p><strong>Tahoma</strong>：十分常见的无衬线字体，被采用为 Windows 2000、Windows XP、Windows Server 2003 及 Sega 游戏主机 Dreamcast 等系统的预设字型，显示效果比<code>Arial</code>要好。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>Verdana</strong>：无衬线字体，优点在于它在小字上仍结构清晰端整、阅读辨识容易。</p>\r\n  </li>\r\n </a><li>\r\n    <p><strong>其他</strong>：Windows 下默认字体列表：\r\n     <a href=\"https://www.microsoft.com/typography/fonts/product.aspx?pid=161\">微软官网</a>、\r\n     <a href=\"https://en.wikipedia.org/wiki/List_of_typefaces_included_with_Microsoft_Windows\">维基百科</a>、\r\n     <a href=\"https://support.office.com/zh-cn/article/%E4%B8%8D%E5%90%8C%E7%89%88%E6%9C%AC%E7%9A%84-Office-%E6%8F%90%E4%BE%9B%E7%9A%84%E5%AD%97%E4%BD%93-db1101fc-5cc0-4300-91cd-de7c79d907cd?CorrelationId=e2918255-27c6-4f99-b24c-6789900e8cb2&amp;ui=zh-CN&amp;rs=zh-CN&amp;ad=CN&amp;ocmsassetID=HA010282644\">Office 字体</a>\r\n   </p>\r\n  </li>\r\n <li>\r\n    <p><strong>结论：<code>微软雅黑</code>为 Win 平台上最值得选择的中文字体，但非游览器默认，需要设置；西文字体的选择以<code>Arial</code>、<code>Tahoma</code>等无衬线字体为主。</strong></p>\r\n  </li>\r\n</ul>\r\n<!--more-->\r\n<h5>2、Mac OS下：</h5>\r\n<ul>\r\n  <li>\r\n    <p><strong>华文黑体（STHeiti）、华文细黑（STXihei）</strong>：属于同一字体家族系列，OS X 10.6 之前的简体中文系统界面默认字体，也是目前 Chrome 游览器下的默认字体，有 <em>Regular</em> 和 <em>Bold</em> 两个字重，显示效果可以接受，<code>华文细黑</code>也曾是我最喜欢的字体之一。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>黑体-简（Heiti SC）</strong>：从 10.6 开始，<code>黑体-简</code>代替<code>华文黑体</code>用作简体中文系统界面默认字体，苹果生态最常用的字体之一，包括 iPhone、iPad 等设备用的也是这款字体，显示效果不错，但是喇叭口设计遭人诟病。</p>\r\n </li>\r\n <li>\r\n    <p><strong>冬青黑体（ Hiragino Sans GB ）</strong>：听说又叫<code>苹果丽黑</code>，日文字体<code>Hiragino KakuGothic</code>的简体中文版，简体中文有 <em>常规体</em> 和 <em>粗体</em> 两种，<code>冬青黑体</code>是一款清新的专业印刷字体，小字号时足够清晰，拥有很多人的追捧。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>Times New Roman</strong>：Mac 平台 Safari 下默认的字体，是最常见且广为人知的西文衬线字体之一，众多网页浏览器和文字处理软件都是用它作为默认字体。</p>\r\n </li>\r\n <li>\r\n    <p><strong>Helvetica、Helvetica Neue</strong>：一种被广泛使用的传奇般的西文字体（这货还有专门的记录片呢），在微软使用山寨货的<code>Arial</code>时，乔布斯却花费重金获得了<code>Helvetica</code>苹果系统上的使用权，因此该字体也一直伴随着苹果用户，是苹果生态中最常用的西文字体。<code>Helvetica Neue</code>为<code>Helvetica</code>的改善版本，且增加了更多不同粗细与宽度的字形，共拥有51种字体版本，极大的满足了日常的使用。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>苹方（PingFang SC）</strong>：在 Mac OS X EL Capitan 上，苹果为中国用户打造了一款全新中文字体--<code>苹方</code>，去掉了为人诟病的喇叭口，整体造型看上去更加简洁，字族共六枚字体：<em>极细体、纤细体、细体、常规体、中黑体、中粗体</em>。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>San Francisco</strong>：同样是 Mac OS X EL Capitan 上最新发布的西文字体，感觉和<code>Helvetica</code>看上去差别不大，目前已经应用在 Mac OS 10.11+、iOS 9.0+、watch OS 等最新系统上。</p>\r\n </li>\r\n <li>\r\n    <p><strong>其他</strong>：Mac 下默认字体列表：\r\n     <a href=\"https://support.apple.com/zh-cn/HT202408\">苹果官网</a>、\r\n      <a href=\"https://en.wikipedia.org/wiki/List_of_typefaces_included_with_OS_X\">维基百科</a>\r\n   </p>\r\n  </li>\r\n <li>\r\n    <p><strong>结论：目前<code>苹方</code>和<code>San Francisco</code>为苹果推出的最新字体，显示效果也最为优雅，但只有最新系统才能支持，而<code>黑体-简</code>和<code>Helvetica</code>可以获得更多系统版本支持，显示效果也相差无几，可以接受。</strong></p>\r\n </li>\r\n</ul>\r\n<h5>3、Android系统：</h5>\r\n<ul>\r\n <li>\r\n    <p><strong>Droid Sans、Droid Sans Fallback</strong>：<code>Droid Sans</code>为安卓系统中默认的西文字体，是一款人文主义无衬线字体，而<code>Droid Sans Fallback</code>则是包含汉字、日文假名、韩文的文字扩展支持。</p>\r\n  </li>\r\n <li>\r\n    <p><strong>结论：<code>Droid Sans</code>为默认的字体，并结合了中英文，无需单独设置。</strong></p>\r\n  </li>\r\n</ul>\r\n<h5>4、iOS系统：</h5>\r\n<ul>\r\n <li>\r\n    <p><strong>iOS 系统的字体和 Mac OS 系统的字体相同，保证了 Mac 上的字体效果，iOS 设备就没有太大问题。</strong></p>\r\n </li>\r\n</ul>\r\n<h5>5、Linux：</h5>\r\n<ul>\r\n <li>\r\n    <p><strong>文泉驿点阵宋体</strong>：类似<code>宋体</code>的衬线字体，一般不推荐使用。</p>\r\n </li>\r\n <li>\r\n    <p><strong>文泉驿微米黑</strong>：几乎是 Linux 社区现有的最佳简体中文字体。</p>\r\n </li>\r\n</ul>\r\n<h3>二、选择字体需要注意的问题</h3>\r\n<h5>1、字体的中英文写法：</h5>\r\n<p>我们在操作系统中常常看到<code>宋体</code>、<code>微软雅黑</code>这样的字体名称，但实际上这只是字体的显示名称，而不是字体文件的名称，一般字体文件都是用英文命名的，如<code>SimSun</code>、<code>Microsoft Yahei</code>。在大多数情况下直接使用显示名称也能正确的显示，但是有一些用户的特殊设置会导致中文声明无效。<br><strong>因此，保守的做法是使用字体的字体名称（英文）或者两者兼写。</strong>如下示例：</p>\r\n<pre><code class=\"CSS\">font-family: STXihei, \"Microsoft YaHei\";\r\nfont-family: STXihei, \"华文细黑\", \"Microsoft YaHei\", \"微软雅黑\";</code></pre>\r\n<h5>2、声明英文字体：</h5>\r\n<p>绝大部分中文字体里都包含英文字母和数字，不进行英文字体声明是没有问题的，但是大多数中文字体中的英文和数字的部分都不是特别漂亮，所以建议也对英文字体进行声明。<br><strong>由于英文字体中大多不包含中文，我们可以先进行英文字体的声明，这样不会影响到中文字体的选择，因此优先使用最优秀的英文字体，中文字体声明则紧随其次。</strong>如下示例：</p>\r\n<pre><code class=\"CSS\">font-family: Arial, \"Microsoft YaHei\";</code></pre>\r\n\r\n<h5>3、照顾不同的操作系统：</h5>\r\n<p>英文、数字部分：在默认的操作系统中，Mac和Win都会带有<code>Arial</code>, <code>Verdana</code>, <code>Tahoma</code>等几个预装字体，从显示效果来看，<code>Tahoma</code>要比<code>Arial</code>更加清晰一些，因此字体设置<code>Tahoma</code>最好放到前面，当找不到<code>Tahoma</code>时再使用<code>Arial</code>；而在 Mac 中，还拥有一款更加漂亮的<code>Helvetica</code>字体，所以为了照顾 Mac 用户有更好的体验，应该更优先设置<code>Helvetica</code>字体；Android&nbsp; &nbsp;系统下默认的无衬线字体就可以接受，因此无需单独设置。<strong>最后，英文、数字字体的最佳写法如下：</strong></p>\r\n<pre><code class=\"CSS\">font-family: Helvetica, Tahoma, Arial;</code></pre>\r\n<p>中文部分：在 Win 下，<code>微软雅黑</code>为大部分人最常使用的中文字体，由于很多人安装 Office 的缘故，Mac 电脑中也会出现微软雅黑字体，因此把显示效果不错的<code>微软雅黑</code>加入到字体列表是个不错的选择；同样，为了保证 Mac 中更为优雅字体<code>苹方（PingFang SC）</code>、<code>黑体-简（Heiti SC）</code>、<code>冬青黑体（ Hiragino Sans GB ）</code>的优先显示，需要把这些字体放到中文字体列表的最前面；同时为了照顾到 Linux 操作系统的体验，还需要添加<code>文泉驿微米黑</code>字体。<strong>最后，中文字体部分最佳写法如下：</strong></p>\r\n<pre><code class=\"CSS\">font-family: \"PingFang SC\", \"Hiragino Sans GB\", \"Heiti SC\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\";</code></pre>\r\n<p><strong>中英文整合写法：</strong></p>\r\n<pre><code class=\"CSS\">font-family: Helvetica, Tahoma, Arial, \"Heiti SC\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\";\r\nfont-family: Helvetica, Tahoma, Arial, \"PingFang SC\", \"Hiragino Sans GB\", \"Heiti SC\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\";</code></pre>\r\n<h5>4、注意向下兼容</h5>\r\n<p>如果还需要考虑旧版本操作系统用户的话，不得不加上一些旧版操作系统存在的字体：Mac 中的<code>华文黑体</code>、<code>冬青黑体</code>，Win中的<code>黑体</code>等。<strong>同样按照显示效果排列在列表后面，写法如下：</strong></p>\r\n<pre><code class=\"CSS\">font-family: Helvetica, Tahoma, Arial, \"PingFang SC\", \"Hiragino Sans GB\", \"Heiti SC\", STXihei, \"Microsoft YaHei\", SimHei, \"WenQuanYi Micro Hei\";</code></pre>\r\n<p>加入了<code> STXihei（华文细黑）</code>和<code> SimHei（黑体）</code>。</p>\r\n<h5>5、补充字体族名称</h5>\r\n<p>字体族大体上分为两类：<code>sans-serif（无衬线体）</code>和<code>serif（衬线体）</code>，当所有的字体都找不到时，我们可以使用字体族名称作为操作系统最后选择字体的方向。一般非衬线字体在显示器中的显示效果会比较好，<strong>因此我们需要在最后添加<code> sans-serif</code>，写法如下：</strong>。</p>\r\n<pre><code class=\"CSS\">font-family: Helvetica, Tahoma, Arial, \"PingFang SC\", \"Hiragino Sans GB\", \"Heiti SC\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\", sans-serif;</code></pre>\r\n<h3>三、我们看一下大公司的常见写法</h3>\r\n<h5>1、小米</h5>\r\n<pre><code class=\"CSS\">font: 14px/1.5 \"Helvetica Neue\",Helvetica,Arial,\"Microsoft Yahei\",\"Hiragino Sans GB\",\"Heiti SC\",\"WenQuanYi Micro Hei\",sans-serif;</code></pre>\r\n<p>小米公司优先使用<code>Helvetica Neue</code>这款字体以保证最新版本 Mac 用户的最佳体验，选择了<code>Arial</code>作为 Win 下默认英文字体及Mac的替代英文字体；中文字体方面首选了<code>微软雅黑</code>，然后选择了<code>冬青黑体</code>及<code>黑体-简</code>作为Mac上的替代方案；最后使用<code>文泉驿微米黑</code>兼顾了 Linux 系统。</p>\r\n<h5>2、淘宝</h5>\r\n<pre><code class=\"CSS\">font: 12px/1.5 tahoma,arial,\'Hiragino Sans GB\',\'\\5b8b\\4f53\',sans-serif;</code></pre>\r\n<p>淘宝网的导航及内容有着大量的衬线字体，鉴于淘宝网站大部分字号比较小，显示效果也还可以接受。代码中可以看出淘宝使用了<code>Tahoma</code>、<code>Arial</code>作为首选英文字体，中文字体首选了<code>冬青黑体</code>，由于 Win 下没有预装该款字体，所以显示出了后面的宋体（<code>5b8b4f53</code>为汉字<code>宋体</code>用 unicode 表示的写法，不用<code>SimSun</code>是因为 Firefox 的某些版本和 Opera 不支持 <code>SimSun</code>的写法）</p>\r\n<h5>3、简书</h5>\r\n<pre><code class=\"CSS\">font-family: \"lucida grande\", \"lucida sans unicode\", lucida, helvetica, \"Hiragino Sans GB\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\", sans-serif;</code></pre>\r\n<p>自认为简书的阅读体验很棒，我们看看简书所用的字体，简书优先选择了<code>lucida</code>家族的系列字体作为英文字体，该系列字体在 Mac 和 Win 上都是预装的，并且有着不俗的表现；中文字体方面将<code>冬青黑体</code>作为最优先使用的字体，同样考虑了Linux系统。</p>\r\n<p><em>各大公司的字体设置大同小异，这里不再一一举例查看，有兴趣的可以自己多多查看。</em></p>\r\n<h3>四、其他的一些注意点</h3>\r\n<h5>1、字体何时需要添加引号</h5>\r\n<p>当字体具体某个取值中若有一种样式名称包含空格，则需要用双引号或单引号表示，如：</p>\r\n<pre><code class=\"CSS\">font-family: \"Microsoft YaHei\", \"Arial Narrow\", sans-serif;</code></pre>\r\n<p>如果书写中文字体名称为了保证兼容性也会添加引号，如：</p>\r\n<pre><code class=\"CSS\">font-family: \"黑体-简\", \"微软雅黑\", \"文泉驿微米黑\";</code></pre>\r\n<h5>2、引用外部字体</h5>\r\n<p>大多数的中文字体由于版权原因不能随意使用，这里推荐一个免版权而且漂亮的中文字体<code>思源黑体</code>，该字体为Adobe与Google推出的一款开源字体， 有七种字体粗细<em>（ExtraLight、Light、Normal、Regular、Medium、Bold 和&nbsp; Heavy）</em>，完全支持日文、韩文、繁体中文和简体中文，字形优美，依稀记得小米公司好像有使用过。<br>鉴于中文字体的体积比较大（一般字库全一点的中文字体动辄几 Mb ），所以较少人会使用外部字体，如果真的需要引入，也可以考虑通过工具根据页面文字的使用多少单独生成中文字体，以减小文件大小。</p>\r\n<h3>五、最后，推荐写法</h3>\r\n<p>由于每个人的审美不一样，钟爱的字体也会有所有不同，这里给出我个人的常用写法：</p>\r\n<p>\r\n</p><pre><code class=\"CSS\">font-family: \"Helvetica Neue\", Helvetica, Arial, \"PingFang SC\", \"Hiragino Sans GB\", \"Heiti SC\", \"Microsoft YaHei\", \"WenQuanYi Micro Hei\", sans-serif;</code></pre>\r\n<p></p>\r\n<blockquote class=\"blockquote\">转载来源：segmentfault 专栏<br>原文作者： koreyhan<br>原文链接：<a href=\"https://segmentfault.com/a/1190000006110417\">https://segmentfault.com/a/1190000006110417</a></blockquote>', 1, 114, '2018-02-27 02:20:15', '2018-03-08 10:05:19', NULL, '2018-02-27 02:20:33', 1),
(2, '实现图片赖加载', '<p>jquery.lazyload 是一个实现图片延迟加载的 jQuery 插件，它可以延迟加载长页面中的图片。在浏览器可视区域外的图片在初始状态下不会被载入，直到用户将页面滚动到它们所在的位置。</p>\r\n<h3>1.引入js文件</h3>\r\n<pre><code><code>&lt;</code>script src=\"//ajax.googleapis.com/ajax/libs/jquery/1.10.2/jquery.min.js\"<code>&gt;</code><code>&lt;</code>/script<code>&gt;</code><br><code>&lt;</code>script src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery.lazyload/1.9.1/jquery.lazyload.min.js\"<code>&gt;</code><code>&lt;</code>/script<code>&gt;</code></code></pre>\r\n<h3>2.img标签</h3>\r\n<pre><code><code>&lt;</code>img src=\"lazy\" data-original=\"images/example.png\" width=\"600\"/<code>&gt;</code></code></pre>\r\n<p>img 标签的 src 指向一个图片占位符，真实图片路径在 data-original 中。</p>\r\n<!--more-->\r\n<h3>3.调用lazyload</h3>\r\n<pre><code>$(\"img.lazy\").lazyload();</code></pre>\r\n<p>图片的占位符除了可以通过 src 属性来指定，也可以通过 lazyload 函数的参数来指定：</p>\r\n<pre><code>$(\"img.lazy\").lazyload({\r\n    placeholder: \"images/placeholder.gif\"\r\n});</code></pre>\r\n<h3>4.占位图片事件触发加载</h3>\r\n<p>如果我们希望滚到到图片的位置后，还要通过 click 或 hover 事件来唤醒图片的加载，我们可以指定 lazyload 函数的 event 属性：</p>\r\n<pre><code>$(\"img.lazy\").lazyload({\r\n    placeholder: \"images/placeholder.gif\",\r\n    event: \"click\"\r\n});</code></pre>\r\n<p>当图片完全加载的时候，插件默认地使用 show() 方法来将图显示出来。你也可以使用其他的效果，如 fadeIn：</p>\r\n<pre><code>$(\"img.lazy\").lazyload({\r\n    effect: \"fadeIn\"\r\n})</code></pre>\r\n<h3>5.提前加载图片</h3>\r\n<p>lazyload 插件默认用户滚动到图片位置时才触发加载图片，如果我们希望滚动到距离图片一定位置就触发加载，可以指定 lazyload 函数的 threshold 参数：</p>\r\n<pre><code>$(“img.lazy”).lazyload({\r\n    threshold: 200\r\n});</code></pre>\r\n<p>这样，当用户滚动到距离图片200像素时图片就开始加载了。</p>\r\n<h3>6.设置查找图片张数</h3>\r\n<p>lazyload 的实现原理是，在页面滚动时，会搜索未加载的图片，如果图片在可视范围内就加载，默认情况下当找到第一张不在可见区域的图片时就会停止搜索。而搜索的顺序就是 HTML 文档中 dom 节点的定义顺序，所以如果有些图片定义在前面，但由于页面不好的布局图片将会呈现在后面，那么就会导致图片没办法正常显示。</p>\r\n<p>此时我们可以通过 lazyload 函数的 failure_limit 参数：</p>\r\n<pre><code>$(\"img.lazy\").lazyload({         \r\n     failure_limit: 10\r\n});</code></pre>\r\n<p>这样一来，插件会搜索至少10个未加载的图片才停止搜索，如果你的图片布局比较不规则，可以尝试将这个参数调大。</p>', 1, 50, '2018-02-28 09:26:20', '2018-03-08 09:59:46', NULL, '2018-02-28 11:10:24', 3);
INSERT INTO `posts` (`id`, `title`, `content`, `status`, `view_count`, `created_at`, `updated_at`, `deleted_at`, `published_at`, `category_id`) VALUES
(3, '推荐几篇前端汇总文章【转】', '<p>\r\n    本篇文章推荐分享五篇关于前端资源汇总的帖子，希望可以帮助到学习前端的你。同时你可以去看一看前端技能树喔：<a href=\"https://link.zhihu.com/?target=http%3A//html5ify.com/fks/fks_chart/\" target=\"_blank\">前端技能</a>\r\n</p>\r\n<h2>第一篇：<a class=\"link\" href=\"https://link.zhihu.com/?target=http%3A//collect.w3ctrain.com/\" target=\"_blank\">前端收藏夹</a></h2>\r\n<p>他们的github地址：<a href=\"https://link.zhihu.com/?target=https%3A//github.com/w3ctrain/w3ctrain.github.io\" target=\"_blank\">GitHub - w3ctrain/w3ctrain.github.io：w3ctrian 前端收藏夹</a>\r\n</p>\r\n<p>以下是目录，更多信息请访问他们的主页：</p>\r\n<h3>一、前端工具<br></h3>\r\n<p>自动化：<a href=\"https://link.zhihu.com/?target=http%3A//gulpjs.com/\" target=\"_blank\">Gulp</a>，<a href=\"https://link.zhihu.com/?target=http%3A//gruntjs.com/\" target=\"_blank\">Grunt</a>，<a href=\"https://link.zhihu.com/?target=http%3A//fis.baidu.com/\" target=\"_blank\">百度Fis</a>，<a href=\"https://link.zhihu.com/?target=http%3A//ant.apache.org/\" target=\"_blank\">Ant</a>，<a href=\"https://link.zhihu.com/?target=http%3A//yeoman.io/\" target=\"_blank\">Yeoman</a>，<a href=\"https://link.zhihu.com/?target=https%3A//incident57.com/codekit/\" target=\"_blank\">Codekit</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/oklai/koala\" target=\"_blank\">Koala</a>，<a href=\"https://link.zhihu.com/?target=https%3A//webpack.github.io/\" target=\"_blank\">Webpack</a>\r\n</p>\r\n<p>预编译：<a href=\"https://link.zhihu.com/?target=https%3A//github.com/slim-template/slim\" target=\"_blank\">Slim</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/haml/haml\" target=\"_blank\">Haml</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/jashkenas/coffeescript\" target=\"_blank\">Coffeescript</a>，<a href=\"https://link.zhihu.com/?target=http%3A//sass-lang.com/\" target=\"_blank\">Sass</a>，<a href=\"https://link.zhihu.com/?target=https%3A//babeljs.io/\" target=\"_blank\">BabelJs</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/jadejs/jade\" target=\"_blank\">Jade</a>，<a href=\"https://link.zhihu.com/?target=http%3A//lesscss.org/\" target=\"_blank\">Less</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/stylus/stylus\" target=\"_blank\">Stylus</a>，<a href=\"https://link.zhihu.com/?target=http%3A//pleeease.io/\" target=\"_blank\">Pleeease</a>，<a href=\"https://link.zhihu.com/?target=https%3A//github.com/postcss/postcss\" target=\"_blank\">PostCSS</a>\r\n</p>\r\n<p>查找工具：<a href=\"https://link.zhihu.com/?target=http%3A//csstriggers.com/\" target=\"_blank\">CssTriggers</a>，<a href=\"https://link.zhihu.com/?target=http%3A//screensiz.es/phone\" target=\"_blank\">screensiz.es</a>，<a href=\"https://link.zhihu.com/?target=http%3A//www.umindex.com/devices/android_resolutions\" target=\"_blank\">友盟指数</a>，<a href=\"https://link.zhihu.com/?target=http%3A//www.javascripting.com/\" target=\"_blank\">javascripting</a>，<a href=\"https://link.zhihu.com/?target=http%3A//builtwith.com/\" target=\"_blank\">builtwith</a>，<a href=\"https://link.zhihu.com/?target=http%3A//thetoolbox.cc/\" target=\"_blank\">thetoolbox</a>，<a href=\"https://link.zhihu.com/?target=https%3A//stylesheets.co/\" target=\"_blank\">stylesheets</a>\r\n</p>\r\n<p>版本控制：<a href=\"https://link.zhihu.com/?target=https%3A//github.com\" target=\"_blank\">Github</a>，<a href=\"https://link.zhihu.com/?target=https%3A//gitcafe.com/\" target=\"_blank\">GitCafe</a>，<a href=\"https://link.zhihu.com/?target=https%3A//about.gitlab.com/\" target=\"_blank\">GitLab</a>，<a href=\"https://link.zhihu.com/?target=https%3A//bitbucket.org/\" target=\"_blank\">Bitbucket</a>，<a href=\"https://link.zhihu.com/?target=https%3A//www.sourcetreeapp.com/\" target=\"_blank\">Sourcetree</a>，<a href=\"https://link.zhihu.com/?target=https%3A//www.zennaware.com/cornerstone/index.php\" target=\"_blank\">Cornerstone</a>，<a href=\"https://link.zhihu.com/?target=http%3A//book.douban.com/subject/4813786/\" target=\"_blank\">版本控制之道</a>，<a href=\"https://link.zhihu.com/?target=http%3A//iissnan.com/pr